import React, { Component } from "react";
import './Regis.css';
import axios from 'axios';

class Regis extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            pw: "",
            pw2: "",
            name: "",
            phone: "",
            email: "",
            address:"",
            checkLogIn: false,
            usernameValid:"*",
            phoneValid:"*",
            emailValid:"*",
            pwValid:"*",
            pw2Valid:"*",
            nameValid:"*"
        }
    }
    getInputUser = (e) => {
        this.setState({
            username: e.target.value
        })
    }
    getInputPw = (e) => {
        this.setState({
            pw: e.target.value
        })
    }
    getInputPw2 = (e) => {
        this.setState({
            pw2: e.target.value
        })
    }
    getInputMail = (e) => {
        this.setState({
            email: e.target.value
        })
    }
    getInputPhone = (e) => {
        this.setState({
            phone: e.target.value
        })
    }
    getInputName = (e) => {
        this.setState({
            name: e.target.value
        })
    }
    getInputAddress = (e) => {
        this.setState({
            address: e.target.value
        })
    }
   
    checkLength = (o, n, min, max) => {
        if (o.length > max || o.length < min) {
            switch(n) {
                case "phoneValid" : this.setState({
                    phoneValid : "Số điện thoại gồm 10-11 kí tự"
                })
                break;
                case "usernameValid" : this.setState({
                    usernameValid : "Tên đăng nhập phải từ 6-16 kí tự"
                })
                break;
                case "pwValid" : this.setState({
                    pwValid : "Mật khẩu phải từ 6-16 kí tự"
                })
                break;
                case "nameValid" : this.setState({
                    nameValid : "Xin nhập họ tên"
                })
                break;
                case "emailValid" : this.setState({
                    emailValid : "Xin nhập Email"
                })
                break;
                default:
                    break;
            }
            
            return false;
        } else {
            switch(n) {
                case "phoneValid" : this.setState({
                    phoneValid : "*"
                })
                break;
                case "usernameValid" : this.setState({
                    usernameValid : "*"
                })
                break;
                case "pwValid" : this.setState({
                    pwValid : "*"
                })
                break;
                case "nameValid" : this.setState({
                    nameValid : "*"
                })
                break;
                case "emailValid" : this.setState({
                    emailValid : "*"
                })
                break;
                default:
                    break;
            }
            return true;
        }
    }
    checkMail = (regexp) => {
        console.log("abc")
        if (!(regexp.test(this.state.email))) {
            this.setState({
                emailValid: "Xin nhập email theo mẫu linhnguyen@gmail.com"
            })
            return false;
        } else {
            this.setState({
                emailValid: "*"
            })
            return true;
        }
    }
  
    addUser = () => {
        var emailRegex = /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*$/;
        var self = this;
        var valid = true;
        valid = valid && self.checkLength(self.state.phone, "phoneValid", 10, 11);
        valid = valid && self.checkLength(self.state.pw, "pwValid", 6, 16);
        valid = valid && self.checkLength(self.state.username, "usernameValid", 6, 16);
        valid = valid && self.checkLength(self.state.name, "nameValid", 1, 1000);
        valid = valid && self.checkMail(emailRegex);
        if(valid)  
        this.setState({
            usernameValid:"*",
            phoneValid:"*",
            emailValid:"*",
            pwValid:"*",
            pw2Valid:"*",
            nameValid:"*"
        })
        if (valid) {
           
            axios.post('http://5ca34ca3190b430014edbc76.mockapi.io/user',
                {
                    name: self.state.name, id: "", phone: self.state.phone, email: self.state.email,
                    username: self.state.username, pw: self.state.pw,address:self.state.address
                }
            )
            alert("Regisitration Success !!!")
        }

    }
    render() {
        console.log(this.state);
        return (

            <div className="Regis">
                <div className="Top">
                    <div>
                        <p>THÔNG TIN CÁ NHÂN</p>
                    </div>
                    <div>
                        <div>
                            <label>Họ và tên <span> {this.state.nameValid}</span></label>
                            <input onChange={this.getInputName} type="text"></input>
                        </div>
                        <div>
                            <label>Số điện thoại <span> {this.state.phoneValid}</span></label>
                            <input onChange={this.getInputPhone} type="text"></input>
                        </div>
                        <div>
                            <label>Địa chỉ Email <span>{this.state.emailValid}</span></label>
                            <input onChange={this.getInputMail} type="text"></input>
                        </div>
                        <div>
                            <label>Username <span> {this.state.usernameValid}</span></label>
                            <input onChange={this.getInputUser} type="text"></input>
                        </div>
                        <div>
                            <label>Địa chỉ</label>
                            <input onChange={this.getInputAddress} type="text"></input>
                        </div>
                        <div className="checkBox">
                            <input type="checkbox"></input>
                            <label>Đăng ký thông tin qua email</label>

                        </div>

                    </div>
                    <div className="Bottom">
                        <div>
                            <p>THÔNG TIN TÀI KHOẢN</p>
                        </div>
                        <div>
                            <div>
                                <label>Mật khẩu <span>{this.state.pwValid}</span></label>
                                <input onChange={this.getInputPw} type="text"></input>
                            </div>
                            <div>
                                <label>Nhập lại mật khẩu <span>{this.state.pw2Valid}</span></label>
                                <input onChange={this.getInputPw2} type="text"></input>
                            </div>
                            <div>
                                <button className="back">QUAY LẠI</button>
                            </div>
                            <div>
                                <button onClick={this.addUser} className="singIn">ĐĂNG KÝ</button>
                            </div>

                        </div>
                    </div>
                </div>
            </div>

        );
    }
}
export default Regis;
